        "id": "urn:uuid:33045210-3ab8-4636-9b2a-c98d22ab915d",
        "label": "2Apps Adminstate",
        "remark": "2 Apps with one pool member disabled",
        "CC_SD": {
            "class": "Tenant",
            "cart": {
                "class": "Application",
                "template": "http",
                "serviceMain": {
                    "class": "Service_HTTP",
                    "virtualAddresses": [
                        "10.1.10.134"
                    ],
                    "pool": "web_pool"
                },
                "web_pool": {
                    "class": "Pool",
                    "monitors": [
                        "http"
                    ],
                    "members": [
                        {
                            "servicePort": 80,
                            "serverAddresses": [
                                "10.1.20.134"
                            ],
                            "adminState": "disable"
                       },
                       {
                            "servicePort": 8082,
                            "serverAddresses": [
                                "10.1.20.134"
                            ]
                        }
                    ]
                }
            },
            "search": {
                "class": "Application",
                "template": "http",
