{
    "class": "AS3",
    "action": "deploy",
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.7.0",
        "id": "fghijkl7890",
        "label": "Sample 2",
        "remark": "HTTP with custom persistence",
        "target": {
            "hostname": "SEA-vBIGIP01.termmarc.com"
        },
        "AppWithAnalytics": {
            "class": "Tenant",
            "Web": {
                "class": "Application",
                "template": "http",
                "statsProfile": {
                     "collectMethod": false,
                     "collectClientSideStatistics": true,
                     "class": "Analytics_Profile",
                     "collectIp": true,
                     "collectPageLoadTime": true,
                     "collectUrl": true,
                     "collectOsAndBrowser": true
                },
                "serviceMain": {
                    "class": "Service_HTTP",
                    "virtualAddresses": [
                        "10.1.10.112"
                    ],
                    "profileAnalytics": {
                        "use": "/AppWithAnalytics/Web/statsProfile"
                    },
                    "pool": "web_pool",
                    "persistenceMethods": [{
                        "use": "jsessionid"
                    }]
                },
                "web_pool": {
                    "class": "Pool",
                    "monitors": [
                        "http"
                    ],
                    "members": [{
                    "servicePort": 8082,
                    "serverAddresses": [
                        "10.1.20.112"
                    ]
                    },
                    {
                    	"servicePort":80,
                    "serverAddresses": [
                    	"10.1.20.112"
                    ]}
                  ]
                },
                "jsessionid": {
                    "class": "Persist",
                    "persistenceMethod": "cookie",
                    "cookieMethod": "hash",
                    "cookieName": "JSESSIONID"
                }
            }
        }
    }
}

{
    "class": "AS3",
    "action": "deploy",
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.7.0",
        "id": "fghijkl7890",
        "label": "Sample 2",
        "remark": "HTTP with custom persistence",
        "target": {
            "hostname": "SEA-vBIGIP01.termmarc.com"
        },
        "HR_App": {
            "class": "Tenant",
            "Web": {
                "class": "Application",
                "template": "http",
                "statsProfile": {
                     "collectMethod": false,
                     "collectClientSideStatistics": true,
                     "class": "Analytics_Profile",
                     "collectIp": true,
                     "collectPageLoadTime": true,
                     "collectUrl": true,
                     "collectMaxTpsAndThroughput": true,
                     "collectUserAgent": true,
                     "collectUserSession": true,
                     "collectMethod": true,
                     "capturedTrafficInternalLogging": true,
                     "captureFilter": {
                     	"requestCapturedParts": "headers",
                     	"responseCapturedParts": "headers"
                     },
                     "collectOsAndBrowser": true
                },
                "serviceMain": {
                    "class": "Service_HTTP",
                    "virtualAddresses": [
                        "10.1.10.113"
                    ],
                    "profileAnalytics": {
                        "use": "/HR_App/Web/statsProfile"
                    },
                    "pool": "web_pool",
                    "persistenceMethods": [{
                        "use": "jsessionid"
                    }]
                },
                "web_pool": {
                    "class": "Pool",
                    "monitors": [
                        "http"
                    ],
                    "members": [{
                    "servicePort": 8082,
                    "serverAddresses": [
                        "10.1.20.113"
                    ]
                    },
                    {
                    	"servicePort":80,
                    "serverAddresses": [
                    	"10.1.20.113"
                    ]}
                  ]
                },
                "jsessionid": {
                    "class": "Persist",
                    "persistenceMethod": "cookie",
                    "cookieMethod": "hash",
                    "cookieName": "JSESSIONID"
                }
            }
        }
    }
}
